mapper.insert:
INSERT INTO BOARD(TITLE, CONTENT, WRITER, REGDATE)
VALUES ( #{title}
       , #{content}
       , #{writer}
       , NOW())

mapper.select:

SELECT NO, TITLE, CONTENT, WRITER, REGDATE, VIEWNO
FROM (
    SELECT @ROWNUM := @ROWNUM + 1 AS RNUM, NO, TITLE, CONTENT, WRITER, REGDATE, VIEWNO
    FROM BOARD, (SELECT @ROWNUM := 0) A
    ORDER BY NO
    ) B
WHERE RNUM
    > (#{pageNum} - 1) * #{amount}
    LIMIT #{amount}

mapper.update:
UPDATE BOARD
SET TITLE   = #{title},
    CONTENT = #{content}
WHERE NO = #{no}

mapper.select:
SELECT ID, PASSWORD
FROM USER
WHERE ID = #{id}
  AND PASSWORD = #{password}
